      <div className="modal-container" onClick={(e) => e.stopPropagation()}> {/* Prevent closing when clicking inside modal */}

        <div className="modal-header">
          <h2>Share Access</h2>
          <button className="modal-close-button" onClick={() => setModalOpen(false)}>
            Ã— {/* HTML entity for 'X' */}
          </button>
        </div>

        <div className="modal-body">
          <label htmlFor="address">Share With (Address):</label>
          <input
            type="text"
            id="address"
            className="address-input" // Use specific class if needed
            placeholder="Enter Ethereum address (0x...)"
            // ... other input props like onChange, value
          />

          {/* Maybe a select dropdown for access type if applicable */}
