          <select id="selectNumber">
             <option value="">Choose Access</option>
             <option value="read">Read-Only</option>
          </select> */}

          <button className="modal-action-button allow-button" onClick={sharing}>
            Allow Access
          </button>

          {/* Section to display shared addresses */}
          <div className="address-list">
            <h3>People With Access:</h3>
            {accessList.length > 0 ? (
              accessList.map((addr, index) => <p key={index}>{addr}</p>)
            ) : (
              <p></p> // Empty paragraph for the ::before pseudo-element styling
            )}
          </div>
        </div>

        {/* Optional Modal Footer for actions */}
        {/* <div className="modal-footer">
            <button className="button secondary" onClick={() => setModalOpen(false)}>Cancel</button>
        </div> */}

      </div>
    </div>
  );
};

export default Modal;
